package exp;

import org.apache.shiro.crypto.AesCipherService;

import java.util.Base64;

public class AesEncrypt {
    public static void main(String[] args) throws Exception {
        // 通过Base64解码获取密钥
        byte[] KEYS = java.util.Base64.getDecoder().decode("kPH+bIxk5D2deZiIxcaaaA==");

        // 创建AES加密服务实例
        AesCipherService acs = new AesCipherService();

        // 原始的Base64加密数据
        String base64data = "";

        // 将Base64字符串转换为字节数组
        byte[] bytedata = java.util.Base64.getDecoder().decode(base64data);

        // 使用AES加密数据
        byte[] encryptedBytes = acs.encrypt(bytedata, KEYS).getBytes();

        // 输出加密后的数据（Base64编码）
        System.out.println(Base64.getEncoder().encodeToString(encryptedBytes));
    }
}
